package com.crazybunqnq.leetcode.algorithm.easy;

import java.util.Arrays;

/**
 * Given two strings s and t which consist of only lowercase letters.
 * <p>
 * String t is generated by random shuffling string s and then add one more letter at a random position.
 * <p>
 * Find the letter that was added in t.
 * <p>
 * Example:
 * <p>
 * Input:
 * s = "abcd"
 * t = "abcde"
 * <p>
 * Output:
 * e
 * <p>
 * Explanation:
 * 'e' is the letter that was added.
 *
 * @author Administrator
 */
public class FindTheDifference {
    public static String s = "";
    public static String t = "";

    public static char findTheDifference_7ms(String s, String t) {
        int n = t.length();
        char c = t.charAt(n - 1);
        for (int i = 0; i < n - 1; ++i) {
            c ^= s.charAt(i);
            c ^= t.charAt(i);
        }
        return c;
    }

    public static char findTheDifference_8ms(String s, String t) {
        char c = 0;
        for (int i = 0; i < s.length(); ++i) {
            c ^= s.charAt(i);
        }
        for (int i = 0; i < t.length(); ++i) {
            c ^= t.charAt(i);
        }
        return c;
    }

    public static char findTheDifference_20ms(String s, String t) {
        char[] ss = s.toCharArray();
        Arrays.sort(ss);
        char[] tt = t.toCharArray();
        Arrays.sort(tt);
        for (int i = 0; i < ss.length; i++) {
            char c = tt[i];
            if (c != ss[i]) {
                return c;
            }
        }
        return tt[tt.length - 1];
    }

    public char findTheDifference_74ms(String s, String t) {
        for (int i = 0; i < s.length(); i++) {
            int index = t.indexOf(s.charAt(i));
            String s1 = t.substring(0, index);
            String s2 = t.substring(index + 1);
            t = s1 + "0" + s2;
        }
        for (int i = 0; i < t.length(); i++) {
            if (t.charAt(i) != '0') {
                return t.charAt(i);
            }
        }
        return ' ';
    }
}
